(26, 'Áo Thun Gân Croptop Tay Dài', 'ao-thun-gan-croptop-tay-dai', '#M25', 'public/uploads/ao-croptop-dai-tay-m25-ava.jpg', 0, 'public/uploads/ao-croptop-dai-tay-m25-cl1.jpg', 'public/uploads/ao-croptop-dai-tay-m25-cl2.jpg', '', 'active', 'shirt', 'Trắng, Đen', '385000', '425000', 'Tín đồ croptop sẽ không thể bỏ qua item siêu xịn xò này, form chuẩn, màu áo nâng tông da, chất thun mịn lạnh mặc vào cảm giác dễ chịu. Bạn có thể phối cùng quần jeans, quần unisex hay đều rất ổn.\r\nTrong ảnh, mẫu phối áo cùng quần dài QHY03 và quần short QS01.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-croptop-dai-tay.jpg\" alt=\"&aacute;o croptop d&agrave;i tay\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-co-tim-tay-dai.jpg\" alt=\"&aacute;o cổ tim tay d&agrave;i\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-nu-tay-dai.jpg\" alt=\"&aacute;o nữ tay d&agrave;i\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-tay-dai.jpg\" alt=\"&aacute;o thun g&acirc;n tay d&agrave;i\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-tay-dai.jpg\" alt=\"&aacute;o thun tay d&agrave;i\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-croptop-tay-dai.jpg\" alt=\"&aacute;o croptop tay d&agrave;i\" /></p>', 'Thun gân', 'HoYang', 'Đỗ Lâm Thành Phát', '66', 'S', 'M', 'L', 'XL', NULL, NULL, '2022-11-14 13:08:15', '2022-11-24 06:39:43'),
(27, 'Áo Kiểu Nữ Dự Tiệc Đầy Sang Chảnh', 'ao-kieu-nu-du-tiec-day-sang-chanh', '#M26', 'public/uploads/ao-kieu-nu-du-tiec-m26-ava.jpg', NULL, 'public/uploads/ao-kieu-nu-du-tiec-m26-cl1.jpg', 'public/uploads/ao-kieu-nu-du-tiec-m26-cl2.jpg', '', 'active', 'shirt', 'Trắng, Đen', '753000', '990000', 'Áo nữ kiểu dự tiệc tay bèo, phối ren lưới thời thượng, sang trọng. Cổ phối thêm viền ren sẽ tô điểm vẻ điệu đà, nữ tính của nàng kết hợp cùng chân váy và chiếc giày cao gót thì bạn càng thêm duyên dáng, xinh đẹp trong các buổi tiệc nhé.\r\nTrong ảnh, mẫu phối áo cùng chân váy V37 và quần lụa ống rộng QHY22.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-kieu-nu-du-tiec.jpg\" alt=\"&aacute;o kiểu nữ dự tiệc\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-kieu-du-tiec.jpg\" alt=\"&aacute;o kiểu dự tiệc\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-trang-tay-canh-doi.jpg\" alt=\"&aacute;o trắng tay c&aacute;nh dơi\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-du-tiec-sang-trong.jpg\" alt=\"&aacute;o dự tiệc sang trọng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-nu-du-tiec-xinh.jpg\" alt=\"&aacute;o nữ dự tiệc xinh\" /></p>', 'Lụa', 'HoYang', 'Đỗ Lâm Thành Phát', '24', 'S', 'M', 'L', 'XL', NULL, NULL, '2022-11-14 13:10:42', '2022-11-22 01:30:10'),
(28, 'Áo Thun Gân Co Giãn Rút Dây Giữa', 'ao-thun-gan-co-gian-rut-day-giua', '#M27', 'public/uploads/ao-thun-gan-co-tim-m27-ava.jpg', NULL, 'public/uploads/ao-thun-gan-co-tim-m27-cl1.jpg', 'public/uploads/ao-thun-gan-co-tim-m27-cl2.jpg', '', 'active', 'shirt', 'Trắng, Đen', '249000', '350000', 'Áo thun gân chạy viền đều khá bắt mắt và dễ mặc. Thiết kế cổ tim phối dây rút sẽ điểm tô thêm sự quyến rũ, trẻ trung và năng động. Bạn có thể mix đồ cùng chân váy, quần jean, quần kaki, legging đều khá tươi trẻ.\r\nTrong ảnh, mẫu phối cùng quần short đính nút QS28 và quần short QSHY04.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-co-tim.jpg\" alt=\"&aacute;o thun g&acirc;n cổ tim\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-rut-giay-giua.jpg\" alt=\"&aacute;o thun r&uacute;t d&acirc;y giữa\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-mau-den.jpg\" alt=\"&aacute;o thun g&acirc;n m&agrave;u đen\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-dep.jpg\" alt=\"&aacute;o thun g&acirc;n đẹp\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-mau-trang.jpg\" alt=\"&aacute;o thun g&acirc;n m&agrave;u trắng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-gan-nu.jpg\" alt=\"&aacute;o thun g&acirc;n nữ\" /></p>\r\n<p>&nbsp;</p>', 'Thun gân nổi', 'HoYang', 'Đỗ Lâm Thành Phát', '49', NULL, 'M', 'L', 'XL', NULL, NULL, '2022-11-14 13:13:34', '2022-11-22 01:31:25'),
(29, 'Áo Thun Kiểu Nữ Cổ Tim Cách Điệu', 'ao-thun-kieu-nu-co-tim-cach-dieu', '#M28', 'public/uploads/ao-thun-co-tim-m28-ava.jpg', NULL, 'public/uploads/ao-thun-co-tim-m28-cl1.jpg', 'public/uploads/ao-thun-co-tim-m28-cl2.jpg', '', 'active', 'shirt', 'Trắng, Đen', '279000', '330000', 'Dù bạn ở độ tuổi nào thì đây cũng là mẫu áo phù hợp với bạn. Sở dĩ mọi người đều ưa chuộng mẫu áo này vì nó dễ phối đồ và lên đồ rất đẹp. Tủ đồ của bạn chắc chắn sẽ phải dành một vị trí cho item này rồi.\r\nTrong ảnh, mẫu kết hợp áo cùng chân váy dáng dài V38 và quần short lưng cao QS28.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-co-tim.jpg\" alt=\"&aacute;o thun cổ tim\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-nu-co-tim.jpg\" alt=\"&aacute;o thun nữ cổ tim\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-den.jpg\" alt=\"&aacute;o thun đen\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-nu-co-tim-cach-dieu.jpg\" alt=\"&aacute;o thun nữ cổ tim c&aacute;ch điệu\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-trang.jpg\" alt=\"&aacute;o thun trắng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-thun-nu-dep.jpg\" alt=\"&aacute;o thun nữ đẹp\" /></p>\r\n<p>&nbsp;</p>', 'Thun', 'HoYang', 'Đỗ Lâm Thành Phát', '57', NULL, 'M', 'L', 'XL', NULL, NULL, '2022-11-14 13:21:41', '2022-11-22 01:30:22'),
(30, 'Áo Lụa Phối Ren Lưới Quyền Qúy', 'ao-lua-phoi-ren-luoi-quyen-quy', '#M29', 'public/uploads/quan-short-nu-de-thuong-m29-ava.jpg', 0, 'public/uploads/quan-short-nu-de-thuong-m29-cl1.jpg', 'public/uploads/quan-short-nu-de-thuong-m29-cl2.jpg', '', 'active', 'shirt', 'Trắng, Đen', '385000', '450000', 'Thiết kế áo lụa không tay phối lưới mềm mại, sang trọng cùng phần phối linh hoạt, tinh tế ở phần cổ phối lưới thanh lịch và cổ điển. Sản phẩm mang đến sự thoải mái, tự tin và lôi cuốn cho người mặc.\r\nÁo phù hợp diện đi chơi, dạo phố hẹn hò cùng bạn bè, gia đình. Thiết kế có thể kết hợp cùng quần short QS28 hoặc quần dài QHY22.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-mau-nude.jpg\" alt=\"quần short nữ m&agrave;u nude\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-de-thuong.jpg\" alt=\"quần short nữ dễ thương\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-mau-nude.jpg\" alt=\"quần short m&agrave;u nude\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-dai-lung-thun.jpg\" alt=\"quần d&agrave;i lưng thun\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-dai-in-hoa-tiet.jpg\" alt=\"quần d&agrave;i in họa tiết\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-dai-nu-dep.jpg\" alt=\"quần d&agrave;i nữ đẹp\" /></p>', 'Lụa', 'HoYang', 'Đỗ Lâm Thành Phát', '62', 'S', 'M', 'L', 'XL', 'XXL', NULL, '2022-11-14 13:26:15', '2023-01-24 11:28:13'),
(31, 'Áo Kiểu Lụa Mỏng Phối Họa Tiết Đẹp', 'ao-kieu-lua-mong-phoi-hoa-tiet-dep', '#M30', 'public/uploads/quan-short-nu-ton-dang-m30-ava.jpg', NULL, 'public/uploads/quan-short-nu-ton-dang-m30-cl1.jpg', 'public/uploads/quan-short-nu-ton-dang-m30-cl2.jpg', '', 'active', 'shirt', 'Xanh, Vàng', '489000', '525000', 'Họa tiết chính là điểm nhấn độc đáo của chiếc áo này, giúp chị em luôn nổi bật, trẻ trung và thời thượng trong mắt bạn bè, đồng nghiệp, hay chính là người bạn đời của bạn.\r\nTrong ảnh, mẫu phối áo cùng quần short đính nút QS28.', '<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-ton-dang.jpg\" alt=\"quần short nữ t&ocirc;n d&aacute;ng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-vang.jpg\" alt=\"quần short nữ v&agrave;ng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-dep.jpg\" alt=\"quần short nữ đẹp\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-lung-cao.jpg\" alt=\"quần short nữ lưng cao\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-dang-rong.jpg\" alt=\"quần short d&aacute;ng rộng\" /></p>\r\n<p><img src=\"https://hoyang.vn/wp-content/uploads/2022/11/quan-short-nu-den.jpg\" alt=\"quần short nữ đen\" /></p>', 'Lụa', 'HoYang', 'Đỗ Lâm Thành Phát', '66', 'S', 'M', NULL, NULL, NULL, NULL, '2022-11-14 13:29:43', '2022-11-22 01:31:58'),
